.section-space40{padding-top:40px;padding-bottom:40px;}
.bg-light{background-color:#f4f4f4;}
.btn{padding:8px 20px;margin-bottom:0;font-size:14px;font-weight:500;border:transparent;border-radius:2px;font-family:Open Sans;text-transform:uppercase;transition:all 0.3s;}
.btn-blue{background-color:#3879d9;color:#fff;font-family:'Open Sans', sans-serif;}
.btn-blue:hover{background-color:#2d67bc;color:#fff;}
.btn-big{padding:12px 50px;}
.btn:focus,.btn:hover{color:#fff!important;text-decoration:none;}
.ripplelink{text-align:center;color:#fff;text-decoration:none;position:relative;overflow:hidden;-webkit-transition:all 0.2s ease;-moz-transition:all 0.2s ease;-o-transition:all 0.2s ease;transition:all 0.2s ease;z-index:0;}
.ripplelink:hover{z-index:1000;}
.bor-r50{border-radius:50px;}
.section-title h2{font-weight:700!important;}
.section-title p{font-weight:normal;font-size:14px;}
.well-box{background-color:#fff;padding:17px;border:1px solid #e9e6e0;margin-bottom:0px;box-shadow:#e2e2e2 1px 1px 6px;}
.feature-info h3{font-size:16px;color:#333;font-weight:600;margin-top:10px;}
.feature-info p{font-size:13px;font-weight:normal;}
.feature-block{min-height:162px;margin-bottom:5px;}
.membership-icon{background:url(https://www.lovevivah.com/assets/images/LV-icons.png);margin:0 auto;}
.micon-contact{background-position:-15px -5px;width:48px;height:48px;}
.micon-email{background-position:-15px -73px;width:48px;height:48px;}
.micon-chat{background-position:-15px -141px;width:48px;height:48px;}
.member-know{font-family:'Open Sans', sans-serif;font-size:16px;font-weight:normal;margin-top:10px;}
